<script setup>
import { DownOutlined, ExclamationCircleOutlined } from '@ant-design/icons-vue';
const displayArray = ref(['intention', 'followStatus', 'sex', 'createPeo', 'createTime', 'intentionCourse', 'reference', 'studentStatus', 'classEndingTime', 'classStopTime'])
const dataSource = ref([{key:1}, {key:2}])
const allColumns = ref([
  {
    title: '上课日期/时段',
    dataIndex: 'classDateTime',
    key: "classDateTime",
    fixed: 'left',
    width: 160,
    required: true // 新增必选标识

  },
  {
    title: '日程类型',
    dataIndex: 'scheduleType',
    key: "scheduleType",
    width: 140
  },
  {
    title: '所属班级/1v1',
    key: 'linkClass1v1',
    dataIndex: 'cloud',
    width: 180
  },
  {
    title: '所属课程',
    key: 'course',
    dataIndex: 'course',
    width: 160

  },
   
  {
    title: '科目',
    dataIndex: 'subject',
    key: "subject",
    width: 110
  },
  {
    title: '点名状态',
    dataIndex: 'callStatus',
    key: 'callStatus',
    width: 130,
  },
  
  {
    title: '出勤率',
    dataIndex: 'attendanceRate',
    key: "attendanceRate",
    width: 150
  },
  {
    title: '消耗数量',
    dataIndex: 'useNum',
    key: "useNum",
    width: 140
  },
  {
    title: '消耗学费',
    dataIndex: 'usePrice',
    key: "usePrice",
    width: 140
  },
  {
    title: '上课老师',
    dataIndex: 'mainTeacher',
    key: "mainTeacher",
    width: 140
  },
  {
    title: '上课助教',
    dataIndex: 'subTeacher',
    key: "subTeacher",
    width: 140
  },
  {
    title: '教师记录课时',
    dataIndex: 'teacherOweNum',
    key: "teacherOweNum",
    width: 140

  },
  {
    title: '创建时间',
    key: 'createTime',
    dataIndex: 'createTime',
    width: 170
  },
  {
    title: '操作',
    dataIndex: 'action',
    key: "action",
    fixed: 'right',
    width: 140,
    required: true
  },
])
const rowSelection = {
  onChange: (selectedRowKeys, selectedRows) => {
    console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
  },
};
const defaultStudentStatus = ref(1)
// 从本地存储读取已保存的列配置
const savedSelected = localStorage.getItem('schedule-latitude');
const keysArray = allColumns.value
  .map(column => column?.key) // 可选链操作符
  .filter(key => typeof key !== 'undefined'); // 过滤未定义的值
const initialSelectedValues = savedSelected
  ? JSON.parse(savedSelected)
  : keysArray;

// 选中的列（初始化包含重要字段）
const selectedValues = ref(initialSelectedValues);
// 生成字段选择选项（排除操作列）
const columnOptions = computed(() =>
  allColumns.value
    .filter(col => col.key !== 'action')
    .map(col => ({
      id: col.key,
      value: col.title,
      disabled: col.required // 禁用必选字段
    }))
);
// 过滤后的列（自动包含必选列）
const filteredColumns = computed(() => {
  const requiredColumns = allColumns.value.filter(col => col.required);
  const optionalColumns = allColumns.value
    .filter(col =>
      selectedValues.value.includes(col.key) &&
      !col.required
    );

  // 保持固定列顺序：left -> normal -> right
  return [
    ...requiredColumns.filter(col => col.fixed === 'left'),
    ...optionalColumns,
    ...requiredColumns.filter(col => col.fixed === 'right')
  ];
});
// 强制包含必选字段的监听
watch(selectedValues, (newVal) => {
  const requiredKeys = allColumns.value
    .filter(col => col.required)
    .map(col => col.key);

  // 自动补全必选字段
  if (!requiredKeys.every(k => newVal.includes(k))) {
    selectedValues.value = Array.from(new Set([
      ...newVal.filter(v => !requiredKeys.includes(v)),
      ...requiredKeys
    ]));
  }
}, { deep: true });
// 自动保存列配置到本地存储
watch(selectedValues, (newVal) => {
  localStorage.setItem('schedule-latitude', JSON.stringify(newVal));
}, { deep: true });
// 表格总宽度计算
const totalWidth = computed(() =>
  filteredColumns.value.reduce((acc, column) => acc + (column.width || 0), 0)
);
</script>

